import React, { useState } from "react";
import { Box, TextField, Typography } from "@material-ui/core";
import { Formik, Form } from "formik";
import * as Yup from "yup";

const ResetPassword = () => {
  const [showPassword, setShowPassword] = useState(false);
  const token = window.localStorage.getItem("token")
  console.log("asdfg",token)

  // Validation schema for the new password and confirm password fields
  const validationSchema = Yup.object().shape({
    newPassword: Yup.string()
      .required("New Password is required.")
      .matches(
        /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/,
        "Please enter a valid password."
      ),
    confirmPassword: Yup.string()
      .required("Confirm Password is required.")
      .oneOf([Yup.ref("newPassword"), null], "Passwords must match"),
  });

  return (
    <Box>
      <Box>
        <Box>
          <Typography variant="h2">Reset your Password</Typography>
          <Typography variant="body2">
            Enter Password and confirm password to set your new password
          </Typography>
          <Formik
            initialValues={{
              newPassword: "",
              confirmPassword: "",
            }}
            validationSchema={validationSchema}
            onSubmit={(values) => {
              // Handle form submission here
              console.log(values);
            }}
          >
            {({ errors, touched, handleChange, handleBlur, values }) => (
              <Form>
                {/* Password field from Login component */}
                <TextField
                  fullWidth
                  variant="outlined"
                  placeholder="Enter new password"
                  type={showPassword ? "text" : "password"}
                  name="newPassword"
                  value={values.newPassword}
                  error={Boolean(touched.newPassword && errors.newPassword)}
                  onBlur={handleBlur}
                  onChange={handleChange}
                />

                {/* Confirm Password field */}
                <TextField
                  fullWidth
                  variant="outlined"
                  placeholder="Confirm new password"
                  type={showPassword ? "text" : "password"}
                  name="confirmPassword"
                  value={values.confirmPassword}
                  error={Boolean(
                    touched.confirmPassword && errors.confirmPassword
                  )}
                  onBlur={handleBlur}
                  onChange={handleChange}
                />

                {/* Toggle show/hide password */}
                <button onClick={() => setShowPassword(!showPassword)}>
                  {showPassword ? "Hide Password" : "Show Password"}
                </button>

                {/* Submit button */}
                <button type="submit">Reset Password</button>
              </Form>
            )}
          </Formik>
        </Box>
      </Box>
    </Box>
  );
};

export default ResetPassword;
