import React, { useContext, useState } from "react";
import {
  Box,
  Button,
  Container,
  FormHelperText,
  Grid,
  TextField,
  Typography,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { Formik, Form, Field } from "formik";
import * as Yup from "yup";
import PhoneInput from "react-phone-input-2";
import "react-phone-input-2/lib/style.css";
import { Link, useHistory } from "react-router-dom";
import axios from "axios";
import apiConfig from "src/ApiConfig/apiConfig";
import { toast } from "react-hot-toast";
import { AuthContext } from "src/context/Auth";

const useStyles = makeStyles((theme) => ({
  // signUpsection: {
  //   height: "100vh",
  //   width: "100vw",
  // },
  textField: {
    // width: "100%",
  },
  errorText: {
    color: "red",
    fontSize: "0.73rem",
  },
  phoneInput: {
    width: "100%",
  },
}));

const SignUp = () => {
  const classes = useStyles();
  const history = useHistory();
  const [isLoading, setIsLoading] = useState(false);
  const auth=useContext(AuthContext)

  const formInitialSchema = {
    firstName: "",
    lastName: "",
    countryCode: "",
    mobileNumber: "",
    email: "",
    password: "",
    country: "",
    companyName: "",
    tinNumber: "",
    gstNumber: "",
    state: "",
    address: "",
    city: "",
    zipCode: "",
    dateOfBirth: "",
    deviceToken: "",
    deviceType: "",
  };
  const formValidationSchema = Yup.object().shape({
    firstName: Yup.string()
      .required("Full Name is required")
      .min(3, "Please enter atleast 3 characters.")
      .max(256, "You can enter only 256 characters.")
      // .matches(/^[A-Z][a-zA-Z]*$/, 'Name must start with a capital letter and contain only letters'),
      .matches(/^[A-Z][a-zA-Z]*$/, 'Name must start with a capital letter and contain only letters')
    .matches(/^(?!.*([A-Za-z])\1\1)/, 'Name cannot have the same letter repeated more than 2 times')
    .matches(/^[^0-9]*$/, 'Name cannot contain numbers'),
    lastName: Yup.string()
      .required("Full Name is required")
      .max(30, "Full Name must not exceed 30 characters"),
    countryCode: Yup.string()
      .required("Country code is required")
      .matches(/^\+?[1-9]\d{0,3}$/, "Invalid country code"),
    mobileNumber: Yup.string()
      .required("Mobile number is required")
      .matches(/^\d{10}$/, "Mobile number must be 10 digits"),
    email: Yup.string()
      .email("Invalid email address")
      .required("Email is required"),
    password: Yup.string()
      .required("Password is required")
      .min(8, "Password must be at least 8 characters long")
      .matches(
        /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[a-zA-Z\d@$!%*?&]+$/,
        "Password must include at least one uppercase letter, one lowercase letter, one number, and one special character"
      ),
    dateOfBirth: Yup.date()
      .required("Date of Birth is required")
      .max(new Date(), "Invalid date of birth")
      .test(
        "age",
        "You must be at least 18 years old",
        (value) => value && calculateAge(value) >= 18
      ),
    country: Yup.string().required("Country is required"),
    companyName: Yup.string().required("Company Name is required"),
    tinNumber: Yup.string().required("TIN Number is required"),
    gstNumber: Yup.string().required("GST Number is required"),
    state: Yup.string().required("State is required"),
    address: Yup.string().required("Address is required"),
    city: Yup.string().required("City name is required"),
    zipCode: Yup.string().required("Zip Code is required"),
  });
  const handleFormSubmit = async (values) => {
    try {
      console.log(values);
      setIsLoading(true);
      const res = await axios({
        method: "POST",
        url: apiConfig.signUp,
        data: {
          firstName: values.firstName,
          lastName: values.lastName,
          countryCode: values.countryCode,
          mobileNumber: values.mobileNumber,
          email: values.email,
          password: values.password,
          country: values.country,
          companyName: values.companyName,
          tinNumber: values.tinNumber,
          gstNumber: values.gstNumber,
          state: values.state,
          address: values.address,
          city: values.city,
          zipCode: values.zipCode,
          dateOfBirth: values.dateOfBirth,

        },
      });

      if (res.data.responseCode === 200) {
        toast.success(res.data.responseMessage);

        history.push({
          pathname: "/otpVerification",
          state: {
            email: values.email,
          },
        });
      }
      if (res.data.responseCode === 404) {
        toast.error(res.data.responseMessage);
      }
      setIsLoading(false);
    } catch (err) {
      toast.error(err?.responseCode?.data?.responseMessage);
      console.error(err.res);
      setIsLoading(false);
    }
  };
  const calculateAge = (dob) => {
    const today = new Date();
    const birthDate = new Date(dob);
    let age = today.getFullYear() - birthDate.getFullYear();
    const monthDiff = today.getMonth() - birthDate.getMonth();
    if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birthDate.getDate())) {
      age--;
    }
    return age;
  };

  return (
